import React from 'react';
import { useAppDispatch, useSelectorTyped } from '../../hooks/useSelectorTyped';
import { closeModalShow } from '../../store/actions/modalAction';
import './Modal.css'

const Modal: React.FC = () => {
    const show = useSelectorTyped((state) => state.modal.show)
    const dispatch = useAppDispatch();
    console.log(show)

    function closeModal() {
        dispatch(closeModalShow(false))
    }
  
    return (
        <div className='modal' style={!show ? { visibility :"hidden" }:{visibility:'visible'}}>
                <div className='modal-content'  >
             
                <div className='modal-title'>
                <h2>Сортировка</h2>
                 <p onClick={closeModal}>x</p>
                </div>
                <div c></div>
                       
                        <label>  <input type='checkbox'></input>По алфавиту</label>
                        <label>  <input type='checkbox'></input>По дню рождения</label>
                
                </div>
            </div>
    );
};

export default Modal;
