import React, { useEffect, useState } from "react";
import Skeleton, { SkeletonTheme } from "react-loading-skeleton";
import "react-loading-skeleton/dist/skeleton.css";
import { useAppDispatch, useAppSelector } from "../../hooks/useSelectorTyped";
import { sendUserNameThunk } from "../../store/actions/usersAction";
import { User } from "../../types/usersTypes";
import "./UserList.css";

const UserList: React.FC = () => {
  const dispatch = useAppDispatch();
  const userList = useAppSelector((state: any) => state.users);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      dispatch(sendUserNameThunk());
      setLoading(false);
    }, 10000);
  }, [dispatch, loading]);

  // useEffect(() => {
  //   async function fetchData() {
  //     setTimeout(async () => {
  //       const result = await fetchUsers()
  //       const sorted = sotredUserName(result.items)
  //       console.log(sorted)
  //       dispatch(getSortedNameUsers(sorted));

  //       },1000)

  //     }
  //   fetchData();

  //   }, [dispatch]);

  if (loading) {
    return (
      <>
        <div className="cards">
        
          <Skeleton height={20} width={50} count={4} />
          <Skeleton height={100} width={50} />
     
        </div>
        <div className="cards">
       
          <Skeleton count={4} />
          <Skeleton height={100} />
     
        </div>
      
          <div className="cards">
          <Skeleton count={4} />
          <Skeleton height={100} />
          </div>

    
          <div className="cards">
          <Skeleton count={4} />
          <Skeleton height={100} />
          </div>
     
      </>
    );
  }

  return (
    <div className="user-list">
      {userList.map((user: User) => {
        return (
          <div className="cards" key={user.id}>
            <img src={user.avatarUrl} alt="avatar" />
            <p>{user.firstName}</p>
            <p>{user.lastName}</p>
            <p>{user.userTag}</p>
            <p>{user.department}</p>
          </div>
        );
      })}
    </div>
  );
};

export default UserList;
