import { User } from "../../types/usersTypes";
import { Dispatch, } from 'redux';
import { RootState } from "..";
import { fetchUsers } from "../../api/api";

export const getSortedNameUsers = (users:User[]) => ({
    type: 'SORT_NAME_USERS',
    users
  
})

export const getSortedDateUsers = (users: User[]) => ({
    type: 'SORT_DATE_USERS',
    users
})

export function sendUserThunk() {
    return async function (dispatch: Dispatch, getState: () => RootState) {
        const { users } = getState();
        const response = fetchUsers()
        console.log(response)
        console.log('user',users)
        dispatch(getSortedNameUsers(await response))
              
    };
}
