import { ModalAction } from "../../types/allUsersTypes"

export const modilState = {
    show:false
}



export const modalReducer = (state = modilState, action: ModalAction) => {
    console.log(action.show)
    switch (action.type) {
        case 'OPEN_MODAL': {
            return {
                ...state,
                show:true
            }
        }
        case 'CLOSE_MODAL': {
            return {
                ...state,
                show:false
            }
            }
          
        default:
            return state
    }
}
