import React from 'react';
import { useAppDispatch, useSelectorTyped } from '../../hooks/useSelectorTyped';
import { closeModalShow } from '../../store/action/modalAction';
import './Modal.css'


// interface PropsType {
//     closeModal: (x: React.MouseEvent) => void
//     showModal: boolean;
// }

const Modal: React.FC = () => {
    const show = useSelectorTyped((state) => state.modal.show)
    const dispatch = useAppDispatch();
    console.log(show)

    function closeModal() {
        dispatch(closeModalShow(false))
        console.log(show)
    }
    console.log(show)
    return (
      
            <div className='modal' style={show?display:'none'}>
                <div className='modal-content'  >
                    <div>
                        <p>Сортировка</p>
                        <p onClick={closeModal}>x</p>
                        <label>  <input type='checbox'></input>По алфавиту</label>
                        <label>  <input type='checbox'></input>По дню рождения</label>
                    </div>


                </div>
            </div>
      


    );
};

export default Modal;
