import React, { useEffect, useState } from 'react';
import { useAppDispatch, useAppSelector } from '../../hooks/useSelectorTyped';
import { closeModalShow } from '../../store/actions/modalAction';
import { sendUserDateThunk, sendUserNameThunk } from '../../store/actions/usersAction';
import './Modal.css';
import x from './x.svg';

const Modal: React.FC = () => {
    const show = useAppSelector((state: { modal: { show: boolean; }; }) => state.modal.show)
    const [check, setCheck] = useState('name')
    // const users = useSelectorTyped((state) => state.modal.userList)
    const dispatch = useAppDispatch();
    console.log(show)

    useEffect(() => {
        if (check === 'name') {
        dispatch(sendUserNameThunk())
        } else {
            dispatch(sendUserDateThunk)
    }
},[check])

    function closeModal() {
        dispatch(closeModalShow(false))
    }

    function chahgeChecked(e: React.MouseEvent) {
        setCheck(e.target.value)
        setTimeout(() => dispatch(closeModalShow(false)), 100);
        if (check === 'name') {
            dispatch(sendUserNameThunk())
        } else {
            dispatch(sendUserDateThunk())
   }
     
    }

console.log(check)
    return (
        <div className='modal' style={!show ? { visibility: "hidden" } : { visibility: 'visible' }}>
            <div className='modal-content'  >
                <img onClick={closeModal} src={x} alt=''></img>
                <div className='modal-title'><h2>Сортировка</h2></div>

                <div className='modal-radio-button'>
                    <label className="radio"><input onChange={chahgeChecked}  checked={check === 'name' ? true : false} value='name' type="radio" /><span></span><p>По алфавиту</p> </label>
                    <label className="radio"> <input onChange={chahgeChecked}  checked={check === 'day' ? true : false} value='day' type="radio" /><span></span><p>По дню рождения</p></label>
                </div>
            </div>
        </div>
    );
};

export default Modal;
