import React, { useState } from 'react';
import './Sort.css';
import { useAppDispatch, useAppSelector } from '../../hooks/useSelectorTyped';
import { sendUserDateThunk} from '../../store/actions/usersAction';
import { chooseDepartment } from '../../store/actions/sortAction';


const department = {
    all:'Все',
    android: 'Android',
    ios: 'iOS',
    design: 'Дизайн',
    management: 'Менеджмент',
    qa: 'QA',
    back_office: 'Бэк-офис',
    frontend: 'Frontend',
    hr: 'HR',
    pr: 'PR',
    backend: 'Backend',
    support: 'Техподдержка',
    analytics: 'Аналитика',
}


const Sort: React.FC = () => {

    const active = useAppSelector(state => state.sort.department);
    // console.log(active)

    // const radio = useAppSelector(state => state.modal.radio);

    function sortedUserDepartment(e: React.MouseEvent, key: string) {
        const name = (e.target as Element).textContent

        dispatch(chooseDepartment(key,name))
        dispatch(sendUserDateThunk())
        // dispatch(sendUserDepartmentThunk(key))
    }

    function getAllUser(e: React.MouseEvent,key:s) {
        const name = (e.target as Element).textContent
        dispatch(chooseDepartment(key,name))
        dispatch(sendUserDateThunk())
        // setActive(name)
        // radio==='name'?dispatch(sendUserNameThunk()):dispatch(sendUserDateThunk())

    }

    const dispatch = useAppDispatch()
    return (
        <div className='sort'>
            <p className={active === 'Все' ? 'b' : 'a'} onClick={(e) => getAllUser(e,'all')}>Все</p>
            {Object.entries(department).map(([key, value], index) => {
                return <p key={index} className={active === value ? 'b' : 'a'} onClick={(e) => sortedUserDepartment(e, key)}>{value}</p>
            })}
        </div>
    );
};

export default Sort;
