export const usersReducer = (state = usersState, action: UsersActionType): UsersState => {
    console.log(action.users)
    switch (action.type) {
        case "FETCH_USERS":
            return { loading: true, error: null, userList: [] }
            case 'FETCH_USERS_SUCCESS':
            return { loading: false, error: null, userList: action.users }
            case 'FETCH_USERS_ERROR':
            return {loading: false, error: action.error, users: []}
        default:
            return state
    }
   
}
