import { AllUsersAction, AllUsersState, } from "../../types/allUsersTypes";


export const usersState = [];


export const autocompliteReducer = (state = initialState, action: AutocomplitAction): AutocompliteState => {
    console.log(action)
    switch (action.type) {
        case 'ADD_USER':
            return {
                ...state,
                filteredUser: [...action.filteredUser],
                userValue: action.userValue,
                showSuggestions: action.showSuggestions,
                showUser:action.showUser

            };
        default:
            return state;
    }
} 


export const allUsersReducer=(state = usersState, action: AllUsersAction): AllUsersState => {
    switch (action.type) {
        case "GET_ALL_USERS": 
            return [
                ...state,
                action.allUsers
            ]
            default:state
        };
            
    
}
