import React, { useEffect } from 'react';
import { Skeleton } from 'react-loading-skeleton/dist/Skeleton';
import { fetchUsers } from '../../api/api';
import { useAppDispatch, useSelectorTyped } from '../../hooks/useSelectorTyped';
import { getUsers } from '../../store/actions/allUsersAction';
import { User } from '../../types/allUsersTypes';
import './UsersList.css';

const UserList: React.FC = () => {
  const [loading, setLoading] = useState(false);
    
    const dispatch = useAppDispatch();
    const usersList = useSelectorTyped((state:any) => state.allUsers);

    useEffect(() => {
      async function fetchData() {
        setTimeout(async () => {
          const result = await fetchUsers()
        dispatch(getUsers(result.items))
