<Skeleton/>
      }

    return (
        <section className='users-list'>
        {usersList.map((user:User) => {
          return <div>
            <img src={user.avatarUrl} alt='avatar'/>
            <p>{user.firstName}</p>
            <p>{user.lastName}</p>
            <p>{ user.userTag}</p>
            <p>{ user.department}</p>
             </div>
     
           })} 
        </section>
    );
};

export default UserList;
