  android: 'Android',
  ios: 'iOS',
  design: 'Дизайн',
  management: 'Менеджмент',
  qa: 'QA',
  back_office: 'Бэк-офис',
  frontend: 'Frontend',
  hr: 'HR',
  pr: 'PR',
  backend: 'Backend',
  support: 'Техподдержка',
  analytics: 'Аналитика',
}





function App() {

  const userList = useAppSelector((state: any) => state.users);
