import { SortActionType, SortState } from "../../types/sortTypes";

export const sortState = {

};

export function sortReducer(state = sortState, action: SortActionType): SortState {
    switch (action.type) {
        case 'CHOOSE_DEPARTMENT':
            return {
                ...state,
                [action.key]: action.value
            }
        default:
            return state
    }
}




