import React, { useState } from 'react';
import './Sort.css';
import { useAppDispatch } from '../../hooks/useSelectorTyped';
import { sendUserDepartmentThunk, sendUserNameThunk } from '../../store/actions/usersAction';


const department = {
    android: 'Android',
    ios: 'iOS',
    design: 'Дизайн',
    management: 'Менеджмент',
    qa: 'QA',
    back_office: 'Бэк-офис',
    frontend: 'Frontend',
    hr: 'HR',
    pr: 'PR',
    backend: 'Backend',
    support: 'Техподдержка',
    analytics: 'Аналитика',
  }
  

const Sort: React.FC = () => {

    const [active, setActive] = useState('Все');


    function sortedUserDepartment(key:s) {
        dispatch(sendUserDepartmentThunk(key))
    }

    function getAllUser(e: React.MouseEvent) {
        
        dispatch(sendUserNameThunk())
    }

  const  dispatch = useAppDispatch()
    return (
        <div className='sort'>
          <p onClick={(e) =>getAllUser(e)}>Все</p>
          {Object.entries(department).map(([key, value]) => {
            return <p onClick={() => sortedUserDepartment(key)}>{value}</p>
          })}
        </div>
    );
};

export default Sort;
