import React, { useEffect } from 'react';
import { fetchUsers } from '../../api/api';
import { useAppDispatch, useSelectorTyped } from '../../hooks/useSelectorTyped';
import { getUsers } from '../../store/actions/allUsersAction';
import { User } from '../../types/allUsersTypes';
import Skeleton, { SkeletonTheme } from 'react-loading-skeleton'
import './UsersList.css';

const Users: React.FC = () => {
    
    const dispatch = useAppDispatch();
    const usersList = useSelectorTyped((state:any) => state.allUsers);

    useEffect(() => {
      async function fetchData() {
        setTimeout(async () => {
          const result = await fetchUsers()
          console.log(result.items)
        dispatch(getUsers(result.items))
          },10000)
         
        }
        fetchData();
      }, [dispatch]);
    
      if (!usersList) {
        <SkeletonTheme baseColor="#202020" highlightColor="#444">
        <p>
            <Skeleton count={3} />
        </p>
    </SkeletonTheme>
      }

    return (
        <section className='users-list'>
        {usersList.map((user:User) => {
          return
          <div>
            <img src={user.avatarUrl} alt='avatar'/>
            <p>{user.firstName}</p>
            <p>{user.lastName}</p>
            <p>{ user.userTag}</p>
            <p>{ user.department}</p>
             </div>
     
           })} 
        </section>
    );
};

export default Users;
