import React, { useState } from 'react';
import { useAppDispatch, useAppSelector } from '../../hooks/useSelectorTyped';
import { closeModalShow } from '../../store/actions/modalAction';
import './Modal.css';
import x from './x.svg';

const Modal: React.FC = () => {
    const show = useAppSelector((state: { modal: { show: boolean; }; }) => state.modal.show)
    const [check, setCheck] = useState({
        name: true,
        date:false
    })
    // const users = useSelectorTyped((state) => state.modal.userList)
    const dispatch = useAppDispatch();
    console.log(show)

    function closeModal() {
        dispatch(closeModalShow(false))
    }

    function chahgeChecked(e:React.MouseEvent,key:string) {
        setCheck((prev) => {
            return {
                ...prev,
                key
        }
    })
}


    return (
        <div className='modal' style={!show ? { visibility: "hidden" } : { visibility: 'visible' }}>
            <div className='modal-content'  >
            <img onClick={closeModal} src={x} alt=''></img>
                <div className='modal-title'><h2>Сортировка</h2></div>
             
                <div className='modal-radio-button'>
                <label className="radio"><input onClick={(e)=>'name'} checked={check.name} value='name' type="radio" /><span></span><p>По алфавиту</p> </label>
                <label className="radio"> <input onClick={chahgeChecked} checked={check.date} value = 'day'type="radio"/><span></span><p>По дню рождения</p></label>
                </div>
            </div>
        </div>
    );
};

export default Modal;
