import React from 'react';
import './Modal.css'


interface PropsType{
    closeModal: (x: React.MouseEvent) => void
    showModal: boolean;
}

const Modal: React.FC <PropsType>= ({closeModal,showModal}) => {

    return (
        <div className='wrapper-modal'hidden={!showModal}>
     <div className='modal-background' >
            <p>Сортировка</p>
            <p onClick={closeModal}>x</p>
            <label>  <input type='checbox'></input>По алфавиту</label>
           <label>  <input type='checbox'></input>По дню рождения</label>
          
        </div>
        </div>
   
    );
};

export default Modal;
