import { getUsers } from '../../store/actions/allUsersAction';
import { User } from '../../types/allUsersTypes';
import './UserList.css';

const UserList: React.FC = () => {

    
    const dispatch = useAppDispatch();
  const userList = useSelectorTyped((state: any) => state.allUsers);
  


