import { sortedUserDepartment } from './api/api';
import './App.css';
import SearchUser from './components/SearchUser/SearchUser';
import UserList from './components/UsersList/UserList';
import { useAppDispatch, useAppSelector } from './hooks/useSelectorTyped';
import { sendUserDepartmentThunk } from './store/actions/usersAction';

function App() {

  const userList = useAppSelector((state: any) => state.users);
  const dispatch = useAppDispatch();

  function fn(i:any) {
    dispatch(sendUserDepartmentThunk(i.department))
}

  return (
    <div className="App">
      <div className='app-top-bar'>
        <h1 className='title'>Поиск</h1>
        <SearchUser />
        <div className='sort'>
          <p>Все</p>
        {userList.map((i:any) => {
          return <p onClick={()=>fn(i.d)} key={i.id }>{ i.department}</p>
     })}
        </div>
      </div>
      <UserList/>
    </div>
  );
}

export default App;
