function checkDate(user:any) {
 return  new Date(user.birthday).getMonth() + 1 > new Date().getMonth() + 1 ||
  (new Date(user.birthday).getMonth() + 1 === (new Date().getMonth() + 1) &&
    new Date(user.birthday).getDate() >= new Date().getDate())
}


