import React from 'react';
import { useAppDispatch, useSelectorTyped } from '../../hooks/useSelectorTyped';
import { closeModalShow } from '../../store/actions/modalAction';
import './Modal.css';
import x from './x.svg';

const Modal: React.FC = () => {
    const show = useSelectorTyped((state) => state.modal.show)
    const dispatch = useAppDispatch();
    console.log(show)

    function closeModal() {
        dispatch(closeModalShow(false))
    }

    return (
        <div className='modal' style={!show ? { visibility: "hidden" } : { visibility: 'visible' }}>
            <div className='modal-content'  >
                <div className='modal-title'><h2>Сортировка</h2></div>
                <img onClick={closeModal} src={x} alt=''></img>
                <p><label className="radio"><input type="radio" name="g" checked /><span></span> Мужской</label>
                    <label className="radio"><input type="radio" name="g" /><span></span> Женский</label>
                    <label className="radio"><input type="radio" name="g" /><span></span> Не скажу</label></p>

            </div>
        </div>
    );
};

export default Modal;
