import React from 'react';
import { useAppDispatch, useSelectorTyped } from '../../hooks/useSelectorTyped';
import { closeModalShow } from '../../store/actions/modalAction';
import './Modal.css';
import x from './x.svg';

const Modal: React.FC = () => {
    const show = useSelectorTyped((state) => state.modal.show)
    const dispatch = useAppDispatch();
    console.log(show)

    function closeModal() {
        dispatch(closeModalShow(false))
    }

    return (
        <div className='modal' style={!show ? { visibility: "hidden" } : { visibility: 'visible' }}>
            <div className='modal-content'  >
                <div className='modal-title'><h2>Сортировка</h2></div>
                <img onClick={closeModal} src={x} alt=''></img>

                <div>
<input type="radio" name="rb" id="rb1" checked/> <label htmlFor="rb1">Переключатель 1</label>
</div>
<div>
<input type="radio" name="rb" id="rb2"/> <label htmlFor="rb2">Переключатель 2</label>
</div>
<div>
<input type="radio" name="rb" id="rb3"/> <label htmlFor="rb3">Переключатель 3</label>
</div>

            </div>
        </div>
    );
};

export default Modal;
